export default function TextForm(props) {
    const handleUpClick = () => {
        console.log("Uppercase Button Was Clicked!!")
    }

    const [text, setText] = useState('Enter text here')
    // setText("Hello")

  return (
    <div>
        <h1>{props.heading}</h1>
        <div className="mb-3">
            <textarea className="form-control" value={text} id="myBox" rows="8"></textarea>
        </div>
        <button className="btn btn-primary" onChange={handleOnChange} onClick={handleUpClick}>Convert to Uppercase</button>
    </div>
  )
}
